package ms.pe.project.user.models.dao;import ms.pe.project.commonuser.models.entity.User;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.data.repository.query.Param;import org.springframework.data.rest.core.annotation.RepositoryRestResource;import org.springframework.data.rest.core.annotation.RestResource;@RepositoryRestResource(path = "users")public interface UserDao extends PagingAndSortingRepository<User,Long> {    @RestResource(path = "find-user")    public User findByUsername(@Param(("username")) String username);    /*@Query("select u from  User u where u.username=?1")    public User select(String username);*/}