package ms.pe.project.commonuser.models.entity;import javax.persistence.*;import java.io.Serializable;@Entity@Table(name = "roles")public class Role implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    public  Long id;    @Column(unique = true,length = 30)    public String name;    private static final long serialVersionUID = 1L;}