package ms.pe.project.commonuser.models.entity;import javax.persistence.*;import java.io.Serializable;import java.util.List;@Entity@Table(name = "users")public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    public Long id;    @Column(unique = true, length = 20, name = "username")    public String username;    @Column(name = "password", length = 100)    public String password;    @Column(name = "active")    public Boolean active;    @Column(name = "name")    public String name;    @Column(name = "last_name")    public String lastName;    @Column(name = "email", unique = true, length = 100)    public String email;    @ManyToMany(fetch = FetchType.LAZY)    @JoinTable(name = "user_role",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "role_id"),            uniqueConstraints = {@UniqueConstraint(columnNames = {"user_id", "role_id"})}    )    public List<Role> roles;    private static final long serialVersionUID = 1L;}