package ms.pe.project.oauth.clients;import ms.pe.project.commonuser.models.entity.User;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(name = "api-rest-user")public interface UserClient {    @GetMapping("/users/search/find-user")    User findByUsername(@RequestParam String username);}