package ms.pe.project.oauth.clients;import ms.pe.project.commonuser.models.entity.User;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.*;@FeignClient(name = "api-rest-user")public interface UserClient {    @GetMapping("/users/search/find-user")    User findByUsername(@RequestParam String username);    @PutMapping("/users/{id}")    public User update(@RequestBody User user, @PathVariable Long id);}