package pe.spring.project.security.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import pe.spring.project.security.entity.Person;import pe.spring.project.security.service.IPersonService;import java.util.List;@RestController@RequestMapping("/persons")public class PersonController {    @Autowired    private IPersonService service;    @GetMapping("/select")    public ResponseEntity<List<Person>> select() {        return ResponseEntity.ok(service.select());    }}