package pe.spring.project.security.implementation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import pe.spring.project.security.repository.IPersonRepository;import pe.spring.project.security.entity.Person;import pe.spring.project.security.service.IPersonService;import java.util.List;@Servicepublic class PersonServiceImpl implements IPersonService {    @Autowired    private IPersonRepository repository;    @Override    public List<Person> select() {        return repository.findAll();    }    @Override    public Person get(Long id) {        return repository.findById(id).orElse(null);    }    @Override    public Person insert(Person person) {        person.status = "CREATED";        return repository.save(person);    }    @Override    public Person update(Person person, Long id) {        Person db = repository.findById(id).orElse(null);        db.name = person.name;        db.age = person.age;        db.status = "UPDATED";        return repository.save(db);    }    @Override    public Person delete(Long id) {        Person db = repository.findById(id).orElse(null);        db.status = "DELETED";        return repository.save(db);    }}