package pe.spring.project.security.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;import pe.spring.project.security.entity.User;import pe.spring.project.security.repository.IUserRepository;import java.util.ArrayList;import java.util.List;@Servicepublic class UserService implements UserDetailsService {    @Autowired    private IUserRepository repository;    @Override    public UserDetails loadUserByUsername(String s) throws UsernameNotFoundException {        User user = repository.findByUsername(s);        List<GrantedAuthority> roles = new ArrayList<>();        roles.add(new SimpleGrantedAuthority("ADMIN"));        UserDetails userDetails = new                org.springframework.security.core.userdetails.User(user.username, user.password, roles);        return userDetails;    }}