package pe.nicolas.item.clients;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.*;import pe.nicolas.item.models.Product;import java.util.List;@FeignClient(name = "products-service")public interface ProductClientRest {    @GetMapping("/select")    List<Product> select();    @GetMapping("/select/{id}")    Product get(@PathVariable Long id);    @PostMapping("/add")    Product save(@RequestBody Product product);    @PutMapping("/edit/{id}")    Product update(@RequestBody Product product, @PathVariable Long id);    @DeleteMapping("/delete/{id}")    void delete(@PathVariable Long id);}