package pe.nicolas.item.models.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Primary;import org.springframework.stereotype.Service;import pe.nicolas.item.clients.ProductClientRest;import pe.nicolas.item.models.Item;import pe.nicolas.item.models.Product;import java.util.List;import java.util.stream.Collectors;@Service("serviceFeign")@Primarypublic class ItemServiceFeign implements ItemService {    @Autowired    private ProductClientRest clientFeign;    @Override    public List<Item> findAll() {        return clientFeign.select().stream()                .map(product -> new Item(product, 1)).collect(Collectors.toList());    }    @Override    public Item findById(Long id, Integer quantity) {        return new Item(clientFeign.get(id), quantity);    }    @Override    public Product save(Product product) {        return clientFeign.save(product);    }    @Override    public Product update(Product product, Long id) {        return clientFeign.update(product, id);    }    @Override    public void delete(Long id) {        clientFeign.delete(id);    }}