package ms.pe.zuul.filters;import com.netflix.zuul.ZuulFilter;import com.netflix.zuul.context.RequestContext;import com.netflix.zuul.exception.ZuulException;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import javax.servlet.http.HttpServletRequest;public class PostTimeFilter extends ZuulFilter {    private static Logger log= LoggerFactory.getLogger(PreTimeFilter.class);    @Override    public String filterType() {        return "post";    }    @Override    public int filterOrder() {        return 1;    }    @Override    public boolean shouldFilter() {        return true;    }    @Override    public Object run() throws ZuulException {        RequestContext ctx= RequestContext.getCurrentContext();        HttpServletRequest request=ctx.getRequest();        log.info("enter to post");        Long startTime=(Long) request.getAttribute("startTime");        Long endTime=System.currentTimeMillis();        Long totalTime=endTime-startTime;        log.error(String.format("Time on seconds %s",totalTime.doubleValue()/1000.00));        log.error(String.format("Time on milliseconds %s",totalTime));        return null;    }}