package ms.pe.project.item.clients;import ms.pe.project.item.models.entity.Product;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.*;import java.util.List;@FeignClient(name = "api-rest-product")public interface ProductClient {    @GetMapping("/select")    List<Product> select();    @GetMapping("get/{id}")    Product get(@PathVariable Long id);    @PostMapping("/insert")    Product insert(@RequestBody Product product);    @PutMapping("/update/{id}")    Product update(@RequestBody Product product, @PathVariable Long id);    @DeleteMapping("/delete/{id}")    void delete(@PathVariable Long id);}