package ms.pe.project.item.controllers;import ms.pe.project.item.models.entity.Item;import ms.pe.project.item.models.entity.Product;import ms.pe.project.item.models.service.IItemService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import java.util.List;@RefreshScope@RestControllerpublic class ItemController {    @Autowired    @Qualifier("serviceFeign")    private IItemService service;    @GetMapping("/select")    public List<Item> select() {        return service.select();    }    @GetMapping("/get/{id}/quantity/{quantity}")    public Item get(@PathVariable Long id, @PathVariable Integer quantity) {        return service.get(id, quantity);    }    @PostMapping("/insert")    @ResponseStatus(HttpStatus.CREATED)    public Product insert(@RequestBody Product product) {        return service.insert(product);    }    @PutMapping("/update/{id}")    @ResponseStatus(HttpStatus.CREATED)    public Product update(@RequestBody Product product, @PathVariable Long id) {        return service.update(product, id);    }    @DeleteMapping("/delete/{id}")    public void  delete(@PathVariable Long id){        service.delete(id);    }}