package ms.pe.project.item.models.service;import ms.pe.project.item.clients.ProductClient;import ms.pe.project.item.models.entity.Item;import ms.pe.project.item.models.entity.Product;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Primary;import org.springframework.stereotype.Service;import java.util.List;import java.util.stream.Collectors;@Service("serviceFeign")@Primarypublic class ItemService implements IItemService {    @Autowired    private ProductClient client;    @Override    public List<Item> select() {        return client.select()                .stream()                .map(product -> new Item(product, 1))                .collect(Collectors.toList());    }    @Override    public Item get(Long id, Integer quantity) {        return new Item(client.get(id), quantity);    }    @Override    public Product insert(Product product) {        return client.insert(product);    }    @Override    public Product update(Product product, Long id) {        return client.update(product, id);    }    @Override    public void delete(Long id) {        client.delete(id);    }}