package ms.pe.project.product.msapirestproduct.controllers;import ms.pe.project.product.msapirestproduct.models.entity.Product;import ms.pe.project.product.msapirestproduct.models.service.IProductService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestControllerpublic class ProductController {    @Autowired    private IProductService service;    @GetMapping("/select")    public List<Product> select() {        return service.select();    }    @GetMapping("/get/{id}")    public Product get(@PathVariable Long id) {        return service.get(id);    }    @PostMapping("/insert")    public Product insert(@RequestBody Product product) {        return service.insert(product);    }    @PutMapping("/update/{id}")    public Product update(@RequestBody Product product, @PathVariable Long id) {        Product data = service.get(id);        data.name = product.name;        data.price = product.price;        return service.insert(data);    }    @DeleteMapping("/delete/{id}")    public void delete(@PathVariable Long id) {        service.delete(id);    }}