package ms.pe.project.product.msapirestproduct.models.entity;import javax.persistence.*;import java.util.Date;@Entity@Table(name = "products")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    public Long id;    @Column(name = "name")    public String name;    @Column(name = "price")    public Double price;    @Column(name = "created_at")    @Temporal(TemporalType.DATE)    public Date createdAt;}