package ms.pe.project.product.msapirestproduct.models.service;import ms.pe.project.product.msapirestproduct.models.dao.ProductDao;import ms.pe.project.product.msapirestproduct.models.entity.Product;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Servicepublic class ProductServiceImpl implements IProductService {    @Autowired    private ProductDao dao;    @Override    @Transactional(readOnly = true)    public List<Product> select() {        return (List<Product>) dao.findAll();    }    @Override    @Transactional(readOnly = true)    public Product get(Long id) {        return dao.findById(id).orElse(null);    }    @Override    @Transactional    public Product insert(Product product) {        return dao.save(product);    }    @Override    @Transactional    public void delete(Long id) {            dao.deleteById(id);    }}